package api

import (
	"time"

	"github.com/google/uuid"
	"github.com/spiffe/go-spiffe/v2/spiffeid"
)

// This will be replaced by the autogenerated API structs
type JoinToken struct {
	ID            uuid.NullUUID
	TrustDomainID uuid.UUID `json:"trustdomainid"`
	Token         string
	Used          bool
	ExpiresAt     time.Time `json:"expiresat"`
	CreatedAt     time.Time `json:"createdat"`
	UpdatedAt     time.Time `json:"updatedat"`
}

type TrustDomain struct {
	ID                uuid.NullUUID
	Name              spiffeid.TrustDomain `json:"name"`
	Description       string               `json:"description"`
	HarvesterSpiffeID spiffeid.ID          `json:"harvesterSpiffeID"`
	OnboardingBundle  []byte               `json:"onboardingbundle"`
	CreatedAt         time.Time            `json:"createdat"`
	UpdatedAt         time.Time            `json:"updatedat"`
}
